import React from "react";
import { inject, observer } from "mobx-react";
import cursosStore from "./stores/CursosStore";
import "./Menu.css"; // Importa el archivo CSS para el estilo

class Menu extends React.Component {
  nombreRef = React.createRef();
  fotoRef = React.createRef();

  render() {
    return (
      <div>
        <header>
          <h1>Header</h1>
        </header>
        <div className="menu-container">
          <h1>Tengo en total {cursosStore.numeroCursos} cursos</h1>

          <form
            onSubmit={(e) => {
              e.preventDefault();
              cursosStore.agregarCurso({
                nombre: this.nombreRef.current.value,
                foto: this.fotoRef.current.value,
              });
              e.target.reset();
            }}
          >
            <input
              type="text"
              placeholder="Foto URL de curso"
              required
              ref={this.fotoRef}
            />
            <input
              type="text"
              placeholder="Nombre de la curso"
              required
              ref={this.nombreRef}
            />
            <button type="submit">Guardar curso</button>
            <button onClick={() => { cursosStore.borrarCurso(); }}>
              Borrar todos los cursos
            </button>
          </form>
          <ul className="horizontal-list">
            {cursosStore.cursos.map((curso) => (
              <li key={curso.nombre}>
                <h2>{curso.nombre}</h2>
                <img
                  src={curso.foto}
                  alt={curso.nombre}
                  style={{ maxWidth: "150px" }}
                />
                <button
                  onClick={() => {
                    cursosStore.borrarCursoPorNombre(curso.nombre);
                  }}
                >
                  Borrar
                </button>
              </li>
            ))}
          </ul>
        </div>
        <footer>
          <h2>Footer</h2>
        </footer>
      </div>
    );
  }
}

export default inject("CursosStore")(observer(Menu));
